import React, { useState, useEffect } from "react";
import { Form, Button} from 'react-bootstrap';
import axios from "axios";
const HomeScreen =(req, res)=>{


  const [name, setName] = useState("");

  const [email, setEmail] = useState("");

  const [id, setId] = useState("");



    const submitHandler = async(e) => {
        e.preventDefault();
        var url = 'https://ypjm72q65l.execute-api.ap-south-1.amazonaws.com/dev/aws-insert-record';
        axios(url, {
  method: 'POST',
  headers:{
    'X-Requested-With': 'XMLHttpRequest'
  }
}).then(res => res.json())
.then(response => console.log('Success:', response))
.catch(error => console.error('Error:', error));
        // await axios.post(` https://ypjm72q65l.execute-api.ap-south-1.amazonaws.com/dev/aws-insert-record`, {
        //     method: 'POST',
        //     headers: {
        //       'Accept': 'application/json',
        //       'Content-Type': 'application/json'
        //     },      
        //     body: JSON.stringify({id, name, email}) });

      }
    return (
        <> <main className='main'>
            <Form onSubmit={submitHandler}>
            <Form.Group controlId="formBasicPassword">
                    <Form.Label>ID</Form.Label>
                    <Form.Control type="text"
                        placeholder="Enter Unique ID"
                        value={id}
                        onChange={(e) => setId(e.target.value)} />
                </Form.Group>
                <Form.Group controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email"
                        placeholder="Enter email" 
                        value={email}
                        onChange={(e) => setEmail(e.target.value)} />

                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                    <Form.Label>Name</Form.Label>
                    <Form.Control type="name"
                        placeholder="Enter name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}/>
                </Form.Group>

                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>
            </main>
        </>
    )
    const insert_data=req.body
}


export default HomeScreen;
